require("dotenv").config();const mongoose=require("mongoose"),{MONGO_USER,MONGO_PASS,MONGO_DB,MONGO_URL}=process.env,mongoOptions={user:MONGO_USER,pass:MONGO_PASS,dbName:MONGO_DB,writeConcern:"majority",retryWrites:!0,useNewUrlParser:!0};mongoose.connect(MONGO_URL,mongoOptions).then(()=>console.log("¡Conexión exitosa con "+MONGO_DB+"!")).catch(o=>console.log(o)),module.exports=mongoose;