#!/usr/bin/env node
const app=require("./app"),debug=require("debug")("escenarios-360-api:server"),http=require("http"),port=normalizePort(process.env.PORT||"3300"),server=(app.set("port",port),http.createServer(app));function normalizePort(r){var e=parseInt(r,10);return isNaN(e)?r:0<=e&&e}function onError(r){if("listen"!==r.syscall)throw r;var e="string"==typeof port?"Pipe "+port:"Port "+port;switch(r.code){case"EACCES":console.error(e+" requires elevated privileges"),process.exit(1);break;case"EADDRINUSE":console.error(e+" is already in use"),process.exit(1);break;default:throw r}}function onListening(){var r=server.address(),r="string"==typeof r?"pipe "+r:"port "+r.port;debug("Listening on "+r)}server.listen(port),server.on("error",onError),server.on("listening",onListening);